import { useMutation } from '@apollo/client';
import gql from 'graphql-tag';
import useForm from '../lib/useForm';
import Form from './styles/Form';

const CREATE_PRODUCT_MUTATION = gql`
    mutation CREATE_PRODUCT_MUTATION(
        #which variables are getting passed in?
        #what types are they?
        $name: String!
        $description: String!
        $price: Int!
        $image: Upload

    ){
        createProduct(data:{
            name: $name
            description: $description
            price: $price
            status: "AVAILABLE"
            photo: {
                create: {
                    image: $image,
                    altText: $name
                }
            }
        }){
            id
            price
            description	
            name
        }
    }`;

export default function CreateProduct(){

    const{ inputs, handleChange, clearForm, resetForm } = useForm({
        image: '',
        name: 'Tingz',
        price: 342567,
        description: 'Tingz be poppin off',
    });

    const payload = useMutation

    return(
        <Form onSubmit={(e) => e.preventDefault()}>
            <fieldset>
                <label htmlFor='image'>
                    <input 
                        type='file' 
                        name='image' 
                        id='image' 
                        onChange={handleChange}
                        required
                    /> 
                </label>
                <label htmlFor='name'>
                    Name
                    <input 
                        type='text' 
                        name='name' 
                        id='name' 
                        placeholder='Name' 
                        value={inputs.name} 
                        onChange={handleChange}
                    /> 
                </label>
                <label htmlFor='price'>
                    Price
                    <input 
                        type='number'
                        id='price'
                        name='price'
                        placeholder='price'
                        value={inputs.price}
                        onChange={handleChange}
                    />
                </label>
                <label htmlFor='description'>
                    Description
                    <textarea
                        id='description'
                        name='description'
                        placeholder='Description'
                        value={inputs.description}
                        onChange={handleChange}
                    ></textarea>
                </label>
                <button type='submit'>+ Add Product</button>
            </fieldset>
        </Form>
    )
}
