import gql from 'graphql-tag';
import { useQuery, useMutation } from '@apollo/client';

const SINGLE_PRODUCT_QUERY = gql`
    query SINGLE_PRODUCT_QUERY($id: ID!){
        Product(where: {
            id: $id
        }){
            id
            name
            price
            description
        }
    }
`;

const UPDATE_PRODUCT_MUTATION = gql`
    mutation UPDATE_PRODUCT_MUTATION(
        #vars passed in here
        $id: ID!
        $name: String
        $description: String
        $price: Int
    ){
        updateProduct(
            #we need id AND data here since we are updating
            id: $id,
            data: {
                id: $id
                name: $name
                description: $description
                price: $price
            }){
                #get some shit back after updating
                id
                price
                description	
                name
            }
        }
    `;

export default function updateProduct({ id }){
    //1. we need to get the existing product
    const { data, loading, error } = useQuery(SINGLE_PRODUCT_QUERY, { variables: {id}});
    console.log(data, loading, error);
    //2. we need to get the mutation to update the product
    //remember, updateProduct is our setter function
    //we also rename our destructured values WHILE destructuring to avoid naming conflicts since we are using 2 hooks
    const [updateProduct, { data: updateData, error: errorLoading, loading: updateLoading }] = useMutation(UPDATE_PRODUCT_MUTATION, 
        { variables: {
            id,
            //TODO: pass in updates to product here
        },
    });
    //3. we need the form to handle(post) the updates
    return(
        <Form onSubmit={async (e) => {
            e.preventDefault();
            //the data we are sending
            //our function we got from useMutation() hook, we passed our query and vars above
            const res = await updateProduct();
            clearForm();
            //go to that products page
            Router.push({
                pathname: `/product/${res.data.createProduct.id}`
            });
        }}>
            <fieldset disabled={loading} aria-busy={loading}>
                <label htmlFor='image'>
                    <input 
                        type='file' 
                        name='image' 
                        id='image' 
                        onChange={handleChange}
                        required
                    /> 
                </label>
                <label htmlFor='name'>
                    Name
                    <input 
                        type='text' 
                        name='name' 
                        id='name' 
                        placeholder='Name' 
                        value={inputs.name} 
                        onChange={handleChange}
                    /> 
                </label>
                <label htmlFor='price'>
                    Price
                    <input 
                        type='number'
                        id='price'
                        name='price'
                        placeholder='price'
                        value={inputs.price}
                        onChange={handleChange}
                    />
                </label>
                <label htmlFor='description'>
                    Description
                    <textarea
                        id='description'
                        name='description'
                        placeholder='Description'
                        value={inputs.description}
                        onChange={handleChange}
                    ></textarea>
                </label>
                <button type='submit'>+ Add Product</button>
            </fieldset>
        </Form>
    )
}
